      out.write("                        casesSwiper.slidePrev();\r\n");
      out.write("                    } else if (direction === 'next') {\r\n");
      out.write("                        casesSwiper.slideNext();\r\n");
      out.write("                    }\r\n");
      out.write("                }\r\n");
      out.write("            }\r\n");
