      out.write("                // 탭 활성화\r\n");
      out.write("                document.querySelectorAll('.tab-btn').forEach(btn => btn.classList.remove('active'));\r\n");
      out.write("                element.classList.add('active');\r\n");
      out.write("\r\n");
